body {
    font-family: Arial, sans-serif;
    line-height: 1.6;
    color: #333;
}

.header {
    background-color: #f4f4f4;
    padding: 1rem;
    text-align: center;
}

.logo {
    max-width: 100px;
    height: auto;
}

.nav-menu {
    background-color: #333;
    color: #fff;
}

.nav-menu ul {
    list-style-type: none;
    padding: 0;
    display: flex;
    justify-content: center;
}

.nav-menu li {
    padding: 1rem;
}

.nav-menu a {
    color: #fff;
    text-decoration: none;
}

.main-content {
    padding: 2rem;
}

.footer {
    background-color: #333;
    color: #fff;
    text-align: center;
    padding: 1rem;
    position: absolute;
    bottom: 0;
    width: 100%;
}

@media (max-width: 768px) {
    .nav-menu ul {
        flex-direction: column;
    }
}

document.addEventListener('DOMContentLoaded', function() {
    // Mobile menu toggle
    const menuToggle = document.querySelector('.menu-toggle');
    const navMenu = document.querySelector('.nav-menu ul');

    if (menuToggle && navMenu) {
        menuToggle.addEventListener('click', function() {
            navMenu.classList.toggle('show');
        });
    }

    // Form validation
    const forms = document.querySelectorAll('form');
    forms.forEach(form => {
        form.addEventListener('submit', function(event) {
            if (!form.checkValidity()) {
                event.preventDefault();
                event.stopPropagation();
            }
            form.classList.add('was-validated');
        }, false);
    });
});

{% extends "base.html" %}

{% block title %}Home{% endblock %}

{% block content %}
<h2>Welcome to Uly Dala</h2>
<p>Uly Dala is a comprehensive academic journal dedicated to advancing knowledge in various fields. Our mission is to publish high-quality, peer-reviewed research that contributes to the academic community and beyond.</p>

<h3>Latest Articles</h3>
<ul>
    {% for article in latest_articles %}
    <li><a href="{{ url_for('article', article_id=article.id) }}">{{ article.title }}</a> by {{ article.authors }}</li>
    {% endfor %}
</ul>

<h3>Announcements</h3>
<ul>
    <li>Call for Papers: Special Issue on Climate Change - Deadline: September 1, 2023</li>
    <li>Uly Dala now indexed in Scopus and Web of Science</li>
</ul>
